/* This script generates mock data for local development.
   This way you don't have to point to an actual API,
   but you can enjoy realistic, but randomized data,
   and rapid page loads due to local, static data.
 */

var jsf = require('json-schema-faker');
var fs = require('fs');
var chalk = require('chalk');
var paths = require('../../config/paths');
var path = require('path');

function getMockDataSchema() {
  var mockDataSchema = null;
  try {
      mockDataSchema = require(path.join(paths.appSrc, '/api/mockDataSchema.js'));
  }
  catch (e) {
      if (e instanceof Error && e.code === "MODULE_NOT_FOUND") {
        // do nothing. No mockDataSchema provided.
      } else {
        throw e;
      }
  }
  return mockDataSchema;
}

function generate(schema) {
  var json = JSON.stringify(jsf(schema));

  fs.writeFile(path.join(paths.appSrc, "/api/db.json"), json, function (err) {
    if (err) {
      return console.log(chalk.red(err));
    } else {
      console.log(chalk.green("Mock data generated."));
    }
  });
}

module.exports = {
  getMockDataSchema: getMockDataSchema,
  generate: generate
};
