// This file centralizes the ways that we've extended create-react-app for Fusion.
var nodeVersionCheck = require('./nodeVersionCheck');
var mockDataGenerator = require('./mockDataGenerator');
var jsonServer = require('./jsonServer');
var lint = require('./lint');

// Require the user to run a minimum Node version
nodeVersionCheck.checkVersion();

// Generate mock data and start mock API server
// if a mock data schema is supplied.
var mockDataSchema = mockDataGenerator.getMockDataSchema();
if (mockDataSchema) {
  mockDataGenerator.generate(mockDataSchema);
  jsonServer.start();
}

lint.run();
