function getBaseUrl(useMockApi, mockApiBaseUrl, devBaseUrl, prodBaseUrl) {
  const inDevelopment = window.location.hostname === 'localhost';

  if (inDevelopment) {
    return useMockApi ? mockApiBaseUrl : devBaseUrl;
  } else {
    return prodBaseUrl;
  }
}

module.exports = {
  getBaseUrl: getBaseUrl
};
